import React, { useEffect, useState } from "react";
import styled from 'styled-components'
import ArrowDownward from '@material-ui/icons/ArrowDropDown'


function About({setSide}){



    return(
        <Section>
            <Container>
                <Header>
                    <Title>
                        About Me
                    </Title>
                </Header>
                <Summary>
                    <Bio>
                        <Text>
                            My name is Serge Metellus and I am a Software Engineer with a Bachelor of Science in Computer Science from Florida International University. 
                            I am passionate about developing robust and efficient software solutions of exceptional quality. I specialize in building websites using React.js and CSS to optimize websites for UI/UX and conversions. 

                        </Text>
                        <Text>
                            I don’t like to define myself by the work I’ve done. I define myself by the applications that I am capable of building. Skills can be taught, personality is inherent. I prefer to keep learning, continue challenging myself, and create remarkable software.
                        </Text>
                    </Bio>
                </Summary>
                <Icon onClick={() => {setSide('front')}}>
                    <ArrowDownward/>
                </Icon>
                <Ideals>
                    <Ideal>
                        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 300 300" xmlSpace="preserve"><path d="M182.56 102.095a5.171 5.171 0 0 1-4.457-2.532 32.821 32.821 0 0 0-4.986-6.408c-6.176-6.173-14.385-9.574-23.118-9.574a5.178 5.178 0 0 1 0-10.356c11.498 0 22.309 4.478 30.438 12.608a43.08 43.08 0 0 1 6.568 8.439 5.179 5.179 0 0 1-4.445 7.823zM149.999 247.485c-17.959 0-32.57-14.61-32.57-32.568a5.18 5.18 0 0 1 5.178-5.179h21.929a5.179 5.179 0 0 1 0 10.355h-16.141c2.34 9.762 11.141 17.037 21.605 17.037 12.252 0 22.218-9.965 22.218-22.213a5.176 5.176 0 1 1 10.353 0c-.002 17.958-14.611 32.568-32.572 32.568z"/><path d="M162.003 220.093h-1.723a5.177 5.177 0 0 1 0-10.355h1.723a5.179 5.179 0 0 1 0 10.355zM177.39 199.997a5.175 5.175 0 0 1-5.174-5.178c0-15.098 6.247-31.488 15.543-40.788 10.086-10.085 15.642-23.496 15.642-37.761 0-14.264-5.556-27.672-15.642-37.761-10.086-10.085-23.494-15.64-37.761-15.64-14.263 0-27.673 5.555-37.757 15.64-10.087 10.09-15.641 23.498-15.641 37.762 0 14.265 5.554 27.676 15.642 37.761 8.242 8.245 14.085 22.062 15.308 35.61h29.01l10.204-63.78-5.865 2.667a5.176 5.176 0 0 1-5.277-.591L150 123.664l-5.618 4.274a5.18 5.18 0 0 1-5.275.591l-5.866-2.667 8.068 50.433a5.178 5.178 0 0 1-10.226 1.635l-9.594-59.952a5.185 5.185 0 0 1 1.975-4.936 5.188 5.188 0 0 1 5.283-.597l11.828 5.377 6.293-4.783a5.176 5.176 0 0 1 6.268 0l6.292 4.783 11.831-5.377a5.18 5.18 0 0 1 7.255 5.533l-12.427 77.659a5.176 5.176 0 0 1-5.111 4.361h-38.368a5.18 5.18 0 0 1-5.178-5.178c0-12.141-5.262-26.216-12.51-33.465-12.043-12.042-18.673-28.053-18.673-45.084 0-17.029 6.63-33.039 18.673-45.082 12.042-12.041 28.052-18.674 45.08-18.674 17.031 0 33.042 6.633 45.085 18.674 12.041 12.043 18.671 28.053 18.671 45.082 0 17.031-6.63 33.042-18.671 45.084-7.252 7.249-12.515 21.324-12.515 33.465a5.179 5.179 0 0 1-5.18 5.177z"/></svg>
                        <IdealTitle>Innovative</IdealTitle>
                        <Text>Proficient in producing modern, cutting-edge software</Text>
                    </Ideal> 
                    <Ideal>

                        <IdealTitle>User-Friendly</IdealTitle>
                        <Text>Designing practical and intuitive applications is my top priority</Text>
                    </Ideal> 
                    <Ideal>

                        <IdealTitle>Responsive</IdealTitle>
                        <Text>My designs will work on devices of any shape or size</Text>
                    </Ideal> 
                    <Ideal>

                        <IdealTitle>Dynamic</IdealTitle>
                        <Text>Websites don't have to be static. I revel in creating complex and intriguing websites</Text>
                    </Ideal>
                </Ideals>
            </Container>

        </Section>

    )
}
export default About;

const Section = styled.section`
    background-color: rgb(47, 47, 47);
    color: white;
    border-bottom: 1px solid gray;
    height: 100%;
    // width: 400px;
    // height: 400px;
`

const Container = styled.div`
    display: block;
    height: 100%;
    color: white;
    // width: 400px;
    max-width: 540px;
    @media(min-width: 1400px){
        max-width: 1320px;
    }

    margin-right: auto;
    margin-left: auto;
    position: relative; 
    padding: 20px;
    padding-top: 70px;
`
// const About = styled.div`

// `
const Header = styled.div`

`
const Title = styled.h1`

`
const Summary = styled.div`

`
const Bio = styled.div`

`
const Text = styled.p`

`
const Icon = styled.div`
    position: absolute;
    top: 200px;
    left: 100px;
    cursor: pointer;
`
const Ideals = styled.div`
`
const Ideal = styled.div`
`
